import React, { useState, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, DirectionsRenderer, StandaloneSearchBox } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px'
};

const center = {
  lat: 37.7749,  // Default center, San Francisco
  lng: -122.4194
};

const GoogleMapComponent = () => {
  const [map, setMap] = useState(null);
  const [directions, setDirections] = useState(null);
  const [currentLocation, setCurrentLocation] = useState(center);
  const searchBox = useRef(null);

  const onLoad = (ref) => {
    searchBox.current = ref;
  };

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        ({ coords }) => {
          setCurrentLocation({ lat: coords.latitude, lng: coords.longitude });
        },
        (error) => {
          console.error('Error fetching the current location:', error);
        }
      );
    } else {
      console.error('Geolocation is not supported by this browser!');
    }
  }, []);

  const onPlacesChanged = () => {
    const places = searchBox.current.getPlaces();
    if (places && places.length > 0) {
      const destination = places[0].geometry.location;

      const directionsService = new window.google.maps.DirectionsService();
      directionsService.route({
        origin: currentLocation,
        destination: destination,
        travelMode: window.google.maps.TravelMode.DRIVING,
      }, (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirections(result);
        } else {
          console.error(`error fetching directions ${result}`);
        }
      });
    }
  };

  return (
    <LoadScript
      googleMapsApiKey="AIzaSyDuBX2b6y-SpGeuj7KFSgBTrxBoJpV3VQA" // Replace with your actual API key
      libraries={["places"]}
    >
      <div className="map-search-box">
        {children}  
      </div>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={currentLocation}
        zoom={10}
        onLoad={map => setMap(map)}
      >
        {directions && <DirectionsRenderer directions={directions} />}
      </GoogleMap>
    </LoadScript>
  );
}

export default GoogleMapComponent;
